def es_primo(n):
    # Comprobamos si n es 2 (unico primo par)
    if n == 2:
        return True

    # Comprobamos si es menor de 2 o es par
    if n < 2 or not n % 2:
        return False

    # Comprobamos si es divisible entre cualquier entero impar entre 3 y sqrt(n)
    return not any(n % i == 0 for i in range(3, int(n**0.5) + 1, 2))

