def CleanWeight(weight):
    # weight es una cadena de texto
    if not str(weight).isnumeric(): # comprobamos si la cadena de texto no contiene un número, de ser así, devuelve que no es un número (he puesto 'NA' para seguir tu nomenclatura pero debiera ser NaN)
        return 'NA'
    w = int(weight)
    if w in [7777, 9999]:
        return 'NA'
    elif w < 1000:
        return w / 2.2
    elif 9000 < w < 9999:
        return w - 9000
    else:
        return w

